import React, { useState } from 'react'
import formatCurrency from '../util'
import Fade from 'react-reveal/Fade'
import { connect } from 'react-redux'
import { removeFromCart } from '../redux/cart/cart'
import { useForm } from 'react-hook-form'
import { useSelector, useDispatch } from 'react-redux'

const CartHook = () => {
	// constructor(props) {
	// 	super(props)
	// 	this.state = {
	// 		name: '',
	// 		email: '',
	// 		address: '',
	// 		showCheckout: false
	// 	}
	// }
	// handleInput = (e) => {
	// 	this.setState({ [e.target.name]: e.target.value })
	// }
	// createOrder = (e) => {
	// 	e.preventDefault()
	// 	const order = {
	// 		name: this.state.name,
	// 		email: this.state.email,
	// 		address: this.state.address,
	// 		cartItems: this.props.cartItems
	// 	}
	// 	this.props.createOrder(order)
	// }
	// render() {
	const cartItems = useSelector((state) => state.cart.cartItems)
	const dispatch = useDispatch()
	const { handleSubmit, register, error } = useForm()

	const [ name, setName ] = useState('')
	const [ email, setEmail ] = useState('')
	const [ address, setAddress ] = useState('')
	const [ showCheckout, setShowCheckout ] = useState(false)

	const onSubmit = (values) => {
		console.log(values)
	}

	return (
		<div>
			{cartItems.length === 0 ? (
				<div className="cart cart-header">Cart is empty</div>
			) : (
				<div className="cart cart-header">You have {cartItems.length} in the cart </div>
			)}
			<div>
				<div className="cart">
					<Fade left cascade>
						<ul className="cart-items">
							{cartItems.map((item) => (
								<li key={item._id}>
									<div>
										<img src={item.cover_url} alt={item.title} />
									</div>
									<div>
										<div>{item.title}</div>
										<div className="right">
											{formatCurrency(item.price)} x {item.count}{' '}
											<button className="button" onClick={() => dispatch(removeFromCart(item))}>
												Remove
											</button>
										</div>
									</div>
								</li>
							))}
						</ul>
					</Fade>
				</div>
				{cartItems.length !== 0 && (
					<div>
						<div className="cart">
							<div className="total">
								<div>Total: {formatCurrency(cartItems.reduce((a, c) => a + c.price * c.count, 0))}</div>
								<button
									onClick={() => {
										this.setState({ showCheckout: true })
									}}
									className="button primary"
								>
									Proceed
								</button>
							</div>
						</div>
						{this.state.showCheckout && (
							<Fade right cascade>
								<div className="cart">
									<form onSubmit={handleSubmit(onSubmit)}>
										<ul className="form-container">
											<li>
												<label>Email</label>
												<input
													name="email"
													ref={register()}
													type="email"
													onChange={this.handleInput}
												/>
											</li>
											<li>
												<label>Name</label>
												<input name="name" type="text" required onChange={this.handleInput} />
											</li>
											<li>
												<label>Address</label>
												<input
													name="address"
													type="text"
													required
													onChange={this.handleInput}
												/>
											</li>
											<li>
												<button className="button primary" type="submit">
													Checkout
												</button>
											</li>
										</ul>
									</form>
								</div>
							</Fade>
						)}
					</div>
				)}
			</div>
		</div>
	)
}

export default CartHook
