import { useEffect, useState } from 'react'
import { connect } from 'react-redux'
import { fetchData } from '../redux/server/data'
import formatCurrency from '../util'
import Fade from 'react-reveal/Fade'
import Modal from 'react-modal'

const Books = ({ data, fetchData }) => {
	const [ toogleModal, setToogleModal ] = useState()

	useEffect(() => {
		fetchData()
	}, [])
	// openModal = (product) => {
	// 	this.setState({ product })
	// }
	// closeModal = () => {
	// 	this.setState({ product: null })
	// }
	return data.loading ? (
		<h2>Loading</h2>
	) : data.error ? (
		<h2>{data.error}</h2>
	) : (
		<Fade bottom cascade>
			<ul className="books">
				{data.data.data &&
					data.data.data.map((book) => (
						<li key={book.id}>
							<div className="book">
								<a href={'#' + book.id} onClick={() => this.openModal(book)}>
									<img src={book.cover_url} alt={book.title} />
									<p>{book.title}</p>
								</a>
								<div className="book-price">
									<div>{formatCurrency(book.price)}</div>
									{/* <button onClick={() => this.props.addToCart(product)} className="button primary">
									Add To Cart
								</button> */}
								</div>
							</div>
						</li>
					))}
				{/* <div>{data.data.data && console.log(data.data.data)}</div> */}
			</ul>
		</Fade>
	)
}

const mapStateToProps = (state) => {
	return {
		data: state.data
	}
}

const mapDispatchToProps = (dispatch) => {
	return {
		fetchData: () => dispatch(fetchData())
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(Books)
