export const ADD_TO_CART = 'ADD_TO_CART'
export const REMOVE_FROM_CART = 'REMOVE_FROM_CART'

export const addToCart = (book) => (dispatch, getState) => {
	const cartItems = getState().cart.cartItems.slice()
	if (cartItems.length === 0) {
		cartItems.push({ ...book, count: 0 })
	} else {
		cartItems.forEach((item) => {
			let alreadyInCart = false
			if (item.id === book.id) {
				++item.count
				alreadyInCart = true
			} else if (!alreadyInCart) {
				cartItems.push({ ...book, count: 0 })
			}
		})
	}
	// else {
	// 	cartItems.forEach
	// }
	// cartItems.forEach((item) => console.log(item))

	dispatch({
		type: ADD_TO_CART,
		payload: {
			cartItems
		}
	})
	localStorage.setItem('cartItems', JSON.stringify(cartItems))
}

export const removeFromCart = (book) => (dispatch, getState) => {
	const cartItems = getState().cart.cartItems.slice().filter((item) => item._id !== book._id)
	dispatch({
		type: REMOVE_FROM_CART,
		payload: {
			cartItems
		}
	})
	localStorage.setItem('cartItems', JSON.stringify(cartItems))
}
